<style>
@import url("https://fonts.googleapis.com/css2?family=Comfortaa:wght@300..700&display=swap");

body {
  background-color: #000000;
}

body::-webkit-scrollbar {
  background-color: transparent;
  width: 0.3125rem;
}

body::-webkit-scrollbar-thumb {
  background-color: #babac08c;
  border-radius: 1rem;
  transition: background-color ease 0.2s;
}

body::-webkit-scrollbar-thumb:hover {
  background-color: #babac0;
}

body::-webkit-scrollbar-thumb:hover {
  border: 0.25rem solid transparent;
}

body .scrollbar::-webkit-scrollbar-button {
  display: none;
}

::selection {
  background: #ffffff;
  color: #000000;
}

html {
  scroll-behavior: smooth;
}

header {
  font-family: ProximaNova;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 30px 10%;
  tab-size: 4;
  font-feature-settings: normal;
  font-variation-settings: normal;
  color-scheme: dark;
  line-height: inherit;
  color: #fff;
  text-rendering: optimizeLegibility;
  box-sizing: border-box;
  border-width: 0;
  border-style: solid;
  pointer-events: auto;
  position: fixed;
  inset: 0;
  z-index: 20;
  height: 72px;
  width: 100vw;
  border-bottom-width: 1px;
  border-color: #ffffff1a;
  background-color: #0a0a0abf;
}

.logo {
  transition: 0.3s;
  cursor: pointer;
}

.logo:hover {
  transform: scale(1.04);
}

.nav-links a,
.cta,
.overlay-content a {
  font-weight: 500;
  color: #ffffffbf;
  text-decoration: none;
}

.nav-links {
  list-style: none;
  display: flex;
}

.nav-links li {
  padding: 0px 20px;
}

.nav-links li a {
  transition: color 0.3s ease 0s;
}

.nav-links li a:hover {
  color: #ffffff;
}

.contact-us-button {
  background-color: #ffffff;
  border: none;
  border-radius: 0.375rem;
  box-shadow: 0 0 1.5625rem #85858559;
  color: #000000;
  cursor: pointer;
  font-size: 1rem;
  font-style: normal;
  font-weight: 700;
  line-height: 100%;
  padding: 1rem 1.25rem;
  text-decoration: unset;
  text-transform: uppercase;
  transition: 0.2s ease;
  transition-property: background-color, box-shadow;
  display: block;
  white-space: nowrap;
  transition: 0.4s;
}

@media (hover: hover) and (pointer: fine) {
  header .contact-us-button:hover {
    box-shadow: 0 0 1rem #ffffff;
  }
}

@media (max-width: 768px) {
  .contact-us-button {
    display: none;
  }

  .nav-links {
    display: none;
  }

  .burger {
    display: flex;
  }
}

.burger {
  display: none;
  flex-direction: column;
  cursor: pointer;
}

.burger .line {
  height: 3px;
  width: 25px;
  background-color: #ffffffbf;
  margin: 4px;
  transition: all 0.3s ease;
  border-radius: 50px;
}

.burger .line.active:nth-child(1) {
  transform: rotate(45deg) translate(5px, 5px);
}

.burger .line.active:nth-child(2) {
  opacity: 0;
}

.burger .line.active:nth-child(3) {
  transform: rotate(-45deg) translate(5px, -5px);
}

.mobile-menu {
  display: none;
  position: absolute;
  top: 72px;
  right: 0;
  border-bottom-width: 1px;
  border-color: #ffffff1a;
  background-color: #0a0a0abf;
  width: 100%;
  z-index: 21;
}

.mobile-menu a {
  font-weight: 500;
  color: #ffffffbf;
  text-decoration: none;
}

.mobile-menu.active {
  display: block;
}

.mobile-menu ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.mobile-menu li {
  padding: 15px;
}

@media (max-width: 768px) {
  .nav-links {
    display: none;
  }

  .burger {
    display: flex;
  }
}

.error {
  align-items: center;
  text-align: center;
  margin-top: 300px;
}

.error-404 {
  font-family: ProximaNova;
  font-size: 100px;
  color: #ffffff;
}

.error-message {
  font-family: ProximaNova;
  font-size: 24px;
  color: #ffffff;
