  cursor: pointer;
  font-size: 1rem;
  font-style: normal;
  font-weight: 700;
  line-height: 100%;
  padding: 1rem 1.25rem;
  text-decoration: unset;
  text-transform: uppercase;
  transition-property: background-color, box-shadow;
  white-space: nowrap;
  margin-top: 20px;
  transition: 0.4s;
}

